package com.example.ahmedyehia.myapplication;

import android.graphics.Typeface;
import android.support.v7.app.AppCompatActivity;
import android.os.Bundle;
import android.widget.TextView;

import org.w3c.dom.Text;

public class Firstbook2 extends AppCompatActivity {

    TextView text1,text2,text3;
    Typeface myfont;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_firstbook2);
        myfont = Typeface.createFromAsset(this.getAssets(),"fonts/me_quran_volt_newmet.ttf");
        text1 = (TextView)findViewById(R.id.text1);
        text2 = (TextView)findViewById(R.id.text2);
        text3 = (TextView)findViewById(R.id.text3);

        text1.setTypeface(myfont);
        text2.setTypeface(myfont);
        text3.setTypeface(myfont);

        text1.setText("لما  لزم  الشيخ  رضي  الله  عنه  العكوف  على  حضرة  ربه،  وترك  المألوف  لطلب  شهوده  وقربه،  أقبل  على  مولاه،  وترك  ما  سواه،  فأقبل  الحق  عليه  وقرَّبه  لديه،  وكشف  له  عن  بديع  جماله،  وأذاقه  حلاوة  وصاله،  وخصَّه  بالنور  المحمدى،  وأدام  له  مشهده  العلى.\n" +
                "روى  ابن  عطاء  الله  رضي  الله  عنه  عن  شيخه  أبى  العباس  المرسي  عن  شيخه  أبى  الحسن  الشاذلي  رضى  الله  عنهم  أنه  قال:\n" +
                "قوي  عليَّ  الشهود  مرة؛  فسألته  أن  يستر  عني  ،  فقيل  لي:\n" +
                "لو  سألته  بما  سأله  موسى  كليمه،  وعيسى  روحه،  ومحمد  صفيه  لم  يفعل،  ولكن  سله  أن  يقويك،  فسألته:  فقواني  .\n" +
                "وفي  ذلك  يقول  الإمام  البوصيري  رضي  الله  عنه:\n" +
                "قد  نال  غاية  ما  يروم  المنتهى\n" +
                "\tمن  ربه  وله  اجتهاد  المبتدى\n" +
                "\n" +
                "متمكن  فى  كل  مشهد  دهشة\n" +
                "\tأو  وقفة  ما  فوقه  من  مشهد\n" +
                "\n" +
                "قل  للمحاول  فى  الدنو  مقامه\n" +
                "\tما  العبد  عند  الله  كالمتعبد\n" +
                "\n" +
                "والفضل  ليس  يناله  متوسل\n" +
                "\tبتورع  كلا  ولا  بتزهد\n" +
                "\n" +
                "إن  قال  ذاك  هو  الدواء  فقل  له\n" +
                "\tكحل  السليم  خلاف  كحل  الأرمد\n" +
                "\n" +
                "    فالقلب  إذا  كان  فارغاً  من  علائق  الحواس  والشهوات  طالع  جواهر  الملكوت،  والطاقة  التى  يطل  منها  المرء  على  عالم  الملكوت  قد  تنفتح  بالنوم  بعجائب  الرؤيا  الصادقة،  وقد  تنفتح  أثناء  اليقظة  لمن  أخلص  النية  وعمل  بالسنة،  ومن  خصائص  الولي  دوام  إشراق  نور  النبوة  على  قلبه    .\n" +
                "ويقول  الإمام  الغزالى  فى  ذلك:\n" +
                "    إن  النفس  مهيأة  بطبعها  لاستقبال  الوحي  والإلهام،  فبطريق  العبادة  والنسك  والرياضة  والمجاهدة  للنفس  ارتفع  حجاب  الحسِّ  المرسل  بين  القلب  واللوح  المحفوظ  وهنالك  تطالع  النفس  جواهر  الملكوت  ومكنونات  الغيب    .\n" +
                "وتوالت  على  الشاذلي  الرؤى  والمكاشفات  تارة  من  الله  عز وجل  وآونة  من  الرسول  صلي  الله  عليه  وسلم،  وأحياناً  من  النبيين  والصديقين  والصالحين.\n \n فى  حضرة  الله \n فأما  مؤانساته  مع  الله  عز  وجل  فكانت  توجيهات  ربانية  لازمة  لأهل  الخصوصية،  ومن  ذلك  قوله  رضي  الله  عنه:\n" +
                "  رأيت  كأني  فى  المحل  الأعلى،  فقلت:    إلهي  أي  الأحوال  أحب  إليك؟  وأي  الأقوال  أصدق  إليك؟  وأي  الأعمال  أدل  على  محبتك؟  فوفقنى  واهدني  \n" +
                "فقيل  لي:\n" +
                "    أحب  الأحوال  إلي:  الرضا  بالمشاهدة؛  وأصدق  الأقوال  لديّ:  قول    لا  إله  إلا  الله    على  الطهارة؛  وأدل  الأعمال  على  محبتي:  بغض  الدنيا،  واليأس  من  أهلها،  مع  الموافقة  \n" +
                "وقال  أيضاً:\n" +
                "    رأيت  كأني  واقف  بين  يدي  ربي  فقال:  لا  تأمن  مكري  في  شيء  وإن  أمنتك،  فإن  علمي  لا  يحيط  به  محيط  \n" +
                "وقال:\n" +
                "    رأيت  كأني  أطوف  بالكعبة  طالباً  من  نفسي  الإخلاص  وأنا  أفتش  عليه  فى  سري؛  فإذا  النداء  عليَّ:  كم  تدندن  مع  من  يدندن  وأنا  السميع  القريب  العليم  الخبير،  وتعريفي  يغنيك  عن  علم  الأولين  والآخرين،  ما  خلا  علم  الرسول  وعلم  النبيين    .\n" +
                "وقال  رضي  الله  عنه:\n" +
                "      قيل  لي  إن  أردت  رضائي  فمن  اسمى  ومنّى  لا  من  اسمك  ومنك،  قلت:  وكيف  ذلك؟  قال:  سبقت  أسمائي  عطائي،  وأسمائي  من  صفاتي،  وصفاتي  قائمة  بذاتي،  ولا  تمحق  ذاتي،  وللعبد  أسماء  دنية،  وأسماء  علية،  فأسماؤه  العلية  قد  وصفه  الله  بها  بقوله  \n" +
                "\uF07D  \uF09A\uF063\uF071\uF0E7\uF036\uF0CD\uF0B3\uF0AF\uF0BB\uF0AD\uF046\uF039\uF024\uF023  \uF09A\uF063صلي  الله  عليه  وسلم\uF0DF\uF089\uF0CE\uF037\uF0BBرضي  الله  عنهم\uF0E8\uF0F8\uF039\uF024\uF023  \uF09A\uF063صلي  الله  عليه  وسلم\uF0DF\uF089\uF0CF\uF04A\uF0BB\uF070رضي  الله  عنه\uF0F8\uF03A\uF024\uF023  \uF09A\uF063\uF071\uF0DF\uF073\uF0CD\uF0B4\uF0AF\uF0BB\uF0A1\uF0A1\uF039\uF024\uF023  \uF09A\uF063\uF071\uF0E3\uF0E8\uF0C5\uF032\uF0BA\uF0A7\uF08D\uF039\uF024\uF023  \uF09A\uF063صلي  الله  عليه  وسلم\uF0DF\uF089\uF0C9  \uF0BB\uF0A1\uF0A1\uF039\uF024\uF023  رضي  الله  عنه\uF062صلي  الله  عليه  وسلم\uF0E3\uF08D\uF0CF\uF042\uF046رضي  الله  عنهم\uF024\uF023  \uF0C5\uF024صلي  الله  عليه  وسلم\uF0E3\uF08D\uF0F7\uF0E8رضي  الله  عنهم\uF04A\uF0F8\uF039\uF024\uF024\uF0CE\uF02F  \uF09A\uF063\uF071\uF0E8  \uF024•\uF059\uF039\uF024\uF023\uF075صلي  الله  عليه  وسلم  \uF0C7\uF060رضي  الله  عنه\uF0E3  \uF0CC\uF08D\uF078\uF036\uF059\uF0DF\uF04A\uF0F8\uF039\uF024\uF023  رضي  الله  عنه\uF062\uF071\uF0DD\uF0E0\uF0CF\uF0FF\uF0BBرضي  الله  عنهم\uF073\uF0F8\uF039\uF024\uF023\uF075صلي  الله  عليه  وسلم  \uF0CF\uF08Aصلي  الله  عليه  وسلم\uF0DF\uF089\uF0E7رضي  الله  عنه\uF0CE\uF03A  \uF0AB\uF021\uF024\uF023  \uF033  \uF0CE\uF08E\uF0C5\uF065\uF0B3\uF06F\uF030\uF075صلي  الله  عليه  وسلم  \uF09A\uF0FA\uF0FC\uF0CF\uF05A\uF0CF\uF042\uF0F7\uF073\uF0DF\uF04A\uF0F8\uF039\uF024\uF023  \uF0C7\uF0CA\uF0CA\uF0CB\uF0C8  \uF07B  [التوبة]،  وبقوله:\uF07D  •\uF062\uF0CE\uF029  \uF09A\uF0FA\uF0FC\uF0CF\uF04A\uF0CE\uF03D\uF0F3\uF0A1\uF0DF\uF04A\uF0F8\uF039\uF024\uF023  \uF0CF\uF04D\uF0BBرضي  الله  عنهم\uF04A\uF0CE\uF03D\uF0F3\uF0A1\uF0DF\uF04A\uF0F8\uF039\uF024\uF023\uF075صلي  الله  عليه  وسلم  \uF09A\uF0FA\uF0FC\uF0CF\uF05A\uF0CF\uF042\uF0F7\uF073\uF0DF\uF04A\uF0F8\uF039\uF024\uF023\uF075صلي  الله  عليه  وسلم  \uF0CF\uF04D\uF0BB\uF06F\uF059\uF0CF\uF042\uF0F7\uF073\uF0DF\uF04A\uF0F8\uF039\uF024\uF023\uF075صلي  الله  عليه  وسلم  رضي  الله  عنه\uF0FB\uF0FC\uF0CF\uF047\uF0CF\uF05A\uF0BB\uF073\uF029\uF0F8\uF039\uF024\uF023\uF075صلي  الله  عليه  وسلم  \uF0CF\uF04D\uF0BBرضي  الله  عنه\uF046\uF0CF\uF05A\uF0BB\uF073\uF029\uF0F8\uF039\uF024\uF023\uF075صلي  الله  عليه  وسلم  رضي  الله  عنه\uF0FB\uF0FC\uF0CF\uF025\uF0CF\uF089\uF0BB\uF0A2\uF0C1\uF039\uF024\uF023\uF075صلي  الله  عليه  وسلم  \uF0CF\uF04D\uF0BB\uF073\uF025\uF0CF\uF089\uF0BB\uF0A2\uF0C1\uF039\uF024\uF023\uF075صلي  الله  عليه  وسلم  رضي  الله  عنه\uF0FB\uF0EF\uF0CE\uF08E\uF0C9\uF039\uF0BB\uF0A2\uF0C1\uF039\uF024\uF023\uF075صلي  الله  عليه  وسلم  \uF0CF\uF04E\uF0BA\uF075\uF08E\uF0C9\uF039\uF0BB\uF0A2\uF0C1\uF039\uF024\uF023\uF075صلي  الله  عليه  وسلم  رضي  الله  عنه\uF0FB\uF0FC\uF0CF\uF0E8\uF0CF\uF0B1\uF0BBرضي  الله  عنهم\uF082\uF0F8\uF039\uF024\uF023\uF075صلي  الله  عليه  وسلم  \uF0CF\uF04D\uF0BBرضي  الله  عنهم\uF0E8\uF0CF\uF0B1\uF0BBرضي  الله  عنهم\uF082\uF0F8\uF039\uF024\uF023\uF075صلي  الله  عليه  وسلم  رضي  الله  عنه\uF0FB\uF0FC\uF0CF\uF025\uF0CF  \uF089\uF07C\uF0C1رضي  الله  عنه\uF046\uF0DF\uF04A\uF0F8\uF039\uF024\uF023\uF075صلي  الله  عليه  وسلم  \uF0CF\uF04D\uF0BB\uF073\uF025\uF0CF  \uF089\uF07C\uF0C1رضي  الله  عنه\uF046\uF0DF\uF04A\uF0F8\uF039\uF024\uF023\uF075صلي  الله  عليه  وسلم  رضي  الله  عنه\uF0FB\uF0FC\uF0CF\uF04A\uF0CD\uF0B4\uF0AF\uF0BB\uF0A2\uF0C1\uF039\uF024\uF023\uF075صلي  الله  عليه  وسلم  \uF0CF\uF04D\uF0BBرضي  الله  عنهم\uF04A\uF0CD\uF0B4\uF0AF\uF0BB\uF0A2\uF0C1\uF039\uF024\uF023\uF075صلي  الله  عليه  وسلم  \uF09A\uF0FA\uF0FC\uF0CF\uF0E0\uF0CF\uF0FF\uF0BB\uF070رضي  الله  عنه\uF0F8\uF03A\uF024\uF023\uF075صلي  الله  عليه  وسلم  \uF0F6\uF04E\uF0DF\uF067رضي  الله  عنهم\uF05Fصلي  الله  عليه  وسلم\uF0E3\uF08D\uF0E8\uF0F9  \uF0CF\uF04D\uF0BB\uF073\uF0E0\uF0CF\uF0FF\uF0BBرضي  الله  عنهم\uF073\uF0F8\uF039\uF024\uF023\uF075صلي  الله  عليه  وسلم  \uF09A\uF0FA\uF0EF\uF0CC\uF08D\uF0C5\uF032\uF0BA\uF0A9\uF025\uF021\uF024\uF023\uF075صلي  الله  عليه  وسلم  \uF0A9\uF021\uF024\uF023  \uF023\uF05A\uF08E\uF08D\uF0CF\uF056\uF078\uF02E  \uF0CF\uF04E\uF0BAرضي  الله  عنه\uF08D\uF0C5\uF032\uF0BA\uF0A9\uF025\uF021\uF024\uF023\uF075صلي  الله  عليه  وسلم  \uF0A3\uF089رضي  الله  عنه\uF0E3صلي  الله  عليه  وسلم\uF026  \uF0AA\uF021\uF024\uF023  \uF04D\uF0E7\uF06C\uF06D\uF03B  \uF05A\uF06Fرضي  الله  عنه\uF08D\uF0CF\uF0FF\uF0F8\uF0F3•\uF042  \uF023•\uF08D\uF0F4\uF05Fصلي  الله  عليه  وسلم\uF026\uF075صلي  الله  عليه  وسلم  \uF024\uF056\uF04A\uF08B\uF0CF\uF0E0رضي  الله  عنه\uF0E3  \uF0C7\uF0CC\uF0CE\uF0C8  \uF07B  [الأحزاب].\n" +
                "وأسماؤه  الدنية  معروفة  كالعاصي  والمذنب  والفاسق  والظالم  وغير  ذلك،  فكما  تمحق  أسماءك  الدنية  بأسمائك  العلية  كذلك  تمحق  أسماؤك  بأسمائه  وصفاتك  بصفاته،  لأن  الحادث  إذا  اقترن  بالقديم  فلا  بقاء  له،  إذا  ناديته  باسمه  كقولك:  يا  غفور  يا  تواب  يا  قريب  يا  وهاب،  فاستدعيت  بها  العطاء  لنفسك،  وقد  تنزلت  لنفسك  من  أسمائه،  وكذلك  إذا  لاحظت  أسمائك  الدنية  من  المعاصى  والظلم  والفسوق  فاشتغلت  بسترها  ومغفرتها  فأنت  باق  مع  نفسك،  وإذا  ناديته  باسمه  العلي  ولاحظت  صفته  العلية  قائمة  بذاته  محقت  أسماءك  كلها  وانعدم  وجودك  فصرت  محوا  لا  وجود  لك  البتة،  فذاك  محل  الفناء  والبقاء  بعد  الفناء  \uF07D  رضي  الله  عنهم\uF037\uF0CF\uF039\uF0BA\uF073\uF08C  \uF0E3\uF040\uF0F4\uF0D2\uF073\uF0F9  \uF0AB\uF021\uF024\uF023  \uF0CF\uF06D\uF08B\uF0CF\uF03F\uF0F7\uF073\uF0E3\uF083  \uF060رضي  الله  عنه\uF042  \uF0E2\uF0E4\uF021\uF024رضي  الله  عنه\uF0B1\uF06F\uF084  \uF034  \uF0AA\uF021\uF024\uF023\uF075صلي  الله  عليه  وسلم  صلي  الله  عليه  وسلم\uF0E8\uF08C  \uF0C8\uF040\uF0F4\uF0D2\uF078\uF0FF\uF0F8\uF039\uF024\uF023  \uF0C9\uF04F\uF08A\uF0CF\uF0E0رضي  الله  عنهم\uF0E8\uF0F8\uF039\uF024\uF023  \uF0C7\uF0CD\uF0C8    \uF07B  [الجمعة]\n" +
                "وقال  أيضاً:  قيل  لي:\n" +
                "    إذا  تداينت  بدين،  فتداين  على  الله،  فإن  تداينت  على  الله  فعلى  الله  أداؤه،  وإن  تداينت  على  نفسك،  أو  على  معلوم  هو  لك،  ثقل  عليك  أداؤه،  وربما  سوّفت  أو  ضيعت،  أو  ماطلت  أو  هونت،  أو  قدمت  أو  أخرت،  أو  ظلمت  أو  كذبت،  فخسرت  وما  ربحت  \n" +
                "فقلت:    وكيف  أتداين  على  الله؟  ،  قال:\n" +
                "  اقطع  النفس  عن  الجهات،  وانتزع  القلب  عن  العادات،  وعلقه  بمن  ملك  الأرض  والسموات،  وقل:  اللهم  عليك  تداينت،  وباسمك  الذي  حملتنى  به  حملت،  وعلى  الله  توكلت،  وإليه  أمري  فوضت،  فأعوذ  بك  من  الدخول  فى  هوى  النفس  والجهل  والفتن  ،  والدنس  والرجس  \n" +
                "فإن  عارضك  عارض  معلوم  هو  لك  من  العادات  التى  تجرى  إليها  نفسك،  فاهرب  إلى  الله  منها  هروبك  من  النار  ومن  عمل  أهل  النار؛  وقل:    أعوذ  بك  من  النار،  ومن  عمل  أهل  النار،  فأنقذني  واغفر  لي  يا  عزيز  يا  غفار  \n" +
                "فهذه  من  غرائب  المعرفة  في  علوم  المعاملة،  فاهرب  عن  نفسك،  واحتسب  أجرك  على  الله.\n" +
                "وقال  رضي  الله  عنه:\n" +
                "    يقول  الله  تعالى:  أنا  وعزتي  وجلالي  لك،  ما  لم  تستبدل  إرادة  الخير  بإرادة  الشر،  أو  تستبدل  حسن  الظن  بكرمى  بسوء  الظن  بي،  أو  تستبدل  التعلق  بي  بالتعلق  بمخلوق  دوني،  فإن  فعلت  ذلك،  تخليت  عنك،  ووكلتك  إلى  نفسك،  ووليتك،  وأصليك  جهنم  وساءت  مصيرا،  فمن  تاب  تاب  الله  عليه،؛  ومن  استغفر،  غفرت  له،  وأنا  الغفور  الرحيم  \n" +
                "ثم  قال:    وعزتي  لولا  خصلتان  فيك،  لأهلكت  بذنوبك  الأمة  \n" +
                "قلت:    وما  هما؟  ،  قال:\n" +
                "    رحمتى  أحب  إليك  من  طاعتي،  واستغفاري  أكثر  لديك  من  معصيتي،  فيهما  سبقت  السابقين،  ولم  أردّك  إلى  المقتصدين،  ولم  ألحقك  بالظالمين  ،  ثم  قال:    قل:  أعوذ  بالله  من  كُمون  الدعوى،  وإرادة  الدنيا،  ومتابعة  الهوى    ،  ثم  قال:    احفظ  هذه،  فهنّ  أصول  الشر  كله،  واستعذ  بالله،  إنه  هو  السميع  العليم  \n" +
                "قال:\n" +
                "    قرأت  ليلة  من  الليالي:  \uF07D  \uF0F6\uF040\uF0E8\uF025  \uF0E8\uF08C\uF071\uF0E3\uF0E3صلي  الله  عليه  وسلم\uF026  \uF0C9\uF062\uF03Eرضي  الله  عنه\uF08D\uF0CE\uF02F  \uF0C4•\uF024•\uF059\uF039\uF024\uF023  \uF0C7\uF0CA\uF0C8..  \uF07B  إلى  أن  انتهيت  إلى  قوله  تعالى:  \uF07D  \uF060\uF0CF\uF042  \uF0CC\uF068\uF08D\uF078\uF0A9  \uF0C4•\uF023\uF075\uF071\uF0F3\uF099\uF075\uF071\uF0F8\uF039\uF024\uF023  \uF0C4•\uF024•\uF059\uF073\uF083\uF0F8\uF03A\uF024\uF023  \uF0C7\uF0CD\uF0C8  \uF093\uF0CF\uF025\uF0A9\uF021\uF024\uF023  \uF0E2•\uF0C8\uF071\uF0F3\uF099\uF075\uF071\uF0E3\uF083  \uF086\uF0CE\uF0FB  \uF0CD\uF091صلي  الله  عليه  وسلم\uF0DF\uF089\uF0DF\uF0B9  \uF0C4\uF05A\uF024•\uF059\uF039\uF024\uF023  \uF0C7\uF0CE\uF0C8  \uF07A\uF060\uF0CF\uF042  \uF0CF\uF070•\uF059\uF0C9  \uF0F8\uF039\uF024\uF023  \uF0C4•\uF024•\uF059\uF039\uF024\uF023\uF075صلي  الله  عليه  وسلم  \uF0C7\uF0CF\uF0C8    \uF07B  [الناس]،  فقيل  لي:  \uF07D  \uF0CC\uF068\uF08D\uF078\uF0A9  \uF0C4•\uF023\uF075\uF071\uF0F3\uF099\uF075\uF071\uF0F8\uF039\uF024\uF023    \uF07B  وسواس  يدخل  بينك  وبين  حبيبك،  يُنسيك  ألطافه  الحسنة،  ويذكرك  أفعالك  السيئة،  ويقلل  عنك  ذات  اليمين،  ويكثر  عليك  ذات  الشمال،  ليعدل  بك  عن  حسن  الظن  بالله  ورسوله،  إلى  سوء  الظن  بالله  ورسوله،  فاحذروا  هذا  الباب  فقد  أخذ  منه  خلق  كثير  من  الزهاد  والعباد  وأهل  الورع  والاجتهاد،  فإذا  أردت  أن  تسلم  من  ذلك  فلا  تدبر  لغد  ولا  لبعد  غد  \n" +
                "قال:  قيل  لي  مرة  :\n" +
                "  ما  الذي  استفدت  من  طاعتي؟  وما  الذي  استفدت  من  معصيتي؟  فقلت:    استفدت  من  الطاعة:  العلم  الزائد،  والنور  النافذ،  والمحبة،  واستفدت  من  المعصية:  الغم  والحزن  والخوف  والرجاء\n");

        text2.setText("مع  الحبيب  المصطفى  صلي  الله  عليه  وسلم \nيقول  الإمام  الغزالي  عن  خبرة  وتجربة  عما  يشاهده  المريد  الصادق  فى  أول  طريقه  إلى  الله:\n" +
                "    ومن  أول  الطريق  تبتدئ  المكاشفات  والمشاهدات،  حتى  أنهم  في  يقظتهم  يشاهدون  الملائكة،  وأرواح  الأنبياء،  ويسمعون  منهم  أصواتاً،  ويقتبسون  منهم  فوائد،  ثم  يترقى  الحال  من  مشاهدة  الصور  والأمثال،  إلى  درجات  يضيق  عنها  نطاق  المنطق    [كتاب  (المنقذ  من  الضلال)  للغزالي.]\n" +
                "ترادفت  المرائي  النبوية  من  رسول  الله  صلي  الله  عليه  وسلم  على  الإمام  الشاذلي  رضي  الله  عنه  للتوجيه  والنصح  والإرشاد  تارة،  ولبيان  الصواب  فى  بعض  الأمور  الملتبسة  عليه  أخرى،  وأحياناً  للبشارة  وغيرها  من  الأحوال.\n" +
                "فمن  ذلك،  قال  رضي  الله  عنه:  رأيت  رسول  الله  صلي  الله  عليه  وسلم  يقول:    أربع  ليس  معهن  من  الفقه  لا  قليل  ولا  كثير:  حب  الدنيا  ونسيان  الآخرة،  وخوف  الفقر  والناس  .\n" +
                "وقال  رضي  الله  عنه:  كنت  متنسكاً  ببعض  الجبال،  فألقى  فى  سّرى:    من  سكن  خوف  الفقر  قلبه،  قلّما  يرفع  له  عمل    فضقت  بذلك  ذرعاً،  وأقمت  على  ذلك  عاماً،  فرأيت  النبي  صلي  الله  عليه  وسلم  يقول  لي:    يا  مبارك  ...  يا  مبارك!  أهلكت  نفسك،  فرق  بين    سكن    و  خطر  ،  فالمؤمن  يخطر  بقلبه،  ولا  يسكن    ...  فسكن  ما  بي.\n" +
                "وقال:  رأيت  النبي  صلي  الله  عليه  وسلم  يقول:\n" +
                "  هُدى  لسنتي  من  آمن  بالله  واليوم  الآخر،  وأعرض  عن  الدنيا،  وأقبل  على  الآخرة،  وعزم  أن  لا  يعصى  الله،  وإن  عصى  استغفر  الله  وتاب  وأناب  ،  قلت:    كيف  تاب  وأناب؟،  قال:    تاب  من  معصية  الله،  وأناب  من  طاعة  الله  إلى  الله  \n" +
                "وقال:  رأيت  رسول  الله  صلي  الله  عليه  وسلم  فقلت:\n" +
                "    يا  رسول  الله،  ما  حقيقة  المتابعة؟،  فقال:    رؤية  المتبوع  عند  كل  شيء،  ومع  كل  شيء،  وفي  كل  شيء  .\n" +
                "وقال:  أوصاني  حبيبي  صلي  الله  عليه  وسلم  فقال:\n" +
                "  لا  تنقل  قدميك  إلا  حيث  ترجو  ثواب  الله،  ولا  تجلس  إلا  حيث  تأمن  من  معصية  الله،  ولا  تصاحب  إلا  من  تستعين  به  على  طاعة  الله،  ولا  تصطف  لنفسك  إلا  من  تزداد  به  يقينا  بالله  ...  وقليل  ما  هم  \n" +
                "وقال:\n" +
                "  كنت  في  مغارة،  فقلت:    إلهى  متى  أكون  لك  عبداً  شاكراً؟  فسمعت  النداء  من  جوف  المغارة:    إذا  لم  تر  فى  الوجود  منعماً  عليك  غيره،  فأنت  إذا  شاكر،  فقلت:    العَاِلم  والمَِلك  أكبر  مني  نعمة  \n" +
                "فقال  لي  النبي  صلي  الله  عليه  وسلم:\n" +
                "    لولا  الأنبياء  لما  اهتديت،  ولولا  العلماء  لما  اقتديت،  ولولا  الملوك  لما  أمنت  –  فالكل  نعمة  مني  عليك  .\n" +
                "وقال:\n" +
                "    قرأت  ليلة  في  وردي:  \uF07D  \uF09F\uF077\uF075صلي  الله  عليه  وسلم  \uF0F4\uF0EC\uF0CE\uF037\uF0AE\uF04B\uF073\uF03F  \uF075\uF0E4\uF021\uF023\uF075\uF071\uF0F7  صلي  الله  عليه  وسلم\uF026  رضي  الله  عنه\uF0FB\uF0EF\uF0CF\uF025\uF0A9\uF021\uF024\uF023  \uF09F\uF077  رضي  الله  عنه\uF062\uF071\uF0DF\uF04A\uF06E\uF03D\uF0F4\uF0E8رضي  الله  عنه\uF083  \uF0C7\uF0CA\uF0D1\uF0C8  \uF0F6\uF04E\uF0E5\uF06B•\uF058\uF0CE\uF029  \uF060\uF073\uF039  \uF028\uF023\uF071\uF0E3\uF05A\uF0F8\uF0F3\uF0E3\uF083  \uF09A\uF081\uF05Aرضي  الله  عنه\uF0E3  \uF07A\uF060\uF0CF\uF042  \uF0AB\uF021\uF024\uF023  \uF024\uF05C\uF0AB\uF0F8\uF08B\uF078\uF0A9    \uF07B  [الجاثية]،  فنمت  فرأيت  رسول  الله  صلي  الله  عليه  وسلم  فقال  لي:    أنا  ممن  يعلم  ولا  أغنى  عنك  من  الله  شيئاً  .\n" +
                "وقال:\n" +
                "  رأيت  النبي  صلي  الله  عليه  وسلم  ونوحاً  -عليه  السلام-  وملكا  بين  أيديهما،  فقال:  لو  علم  نوح  من  قومه،  كما  علم  محمد  صلي  الله  عليه  وسلم  من  قومه،  ما  دعا  عليهم  بقوله:  \uF07D  \uF09F\uF077  \uF0F6\uF091\uF078\uF08B\uF073\uF03F  \uF092\uF06E\uF03Fرضي  الله  عنه\uF0E3  \uF0C7\uF0DA\uF0F6\uF091\uF046\uF07B\uF024\uF023  \uF07A\uF060\uF0CF\uF042  رضي  الله  عنه\uF0FB\uF0EF\uF0CD\uF08D\uF0CF\uF0FF\uF0BB\uF073\uF033\uF0F8\uF039\uF024\uF023  \uF023•\uF091\uF024\uF0AD\uF083رضي  الله  عنهم\uF08A  \uF0C7\uF0CB\uF0CF\uF0C8  رضي  الله  عنهم\uF037•\uF052\uF0CE\uF029  \uF062\uF0CE\uF029  \uF0F6\uF04E\uF0E8  \uF0F6\uF091\uF078\uF08B\uF073\uF03F  \uF028\uF023\uF071\uF09D\uF03D\uF0C5\uF0D2\uF0E3\uF083  \uF09A\uF082رضي  الله  عنهم\uF08A\uF024رضي  الله  عنه\uF036\uF0CF\uF0E3  \uF09F\uF077\uF075صلي  الله  عليه  وسلم  \uF028\uF023\uF0FFصلي  الله  عليه  وسلم\uF0E0\uF024\uF0CE\uF023رضي  الله  عنه\uF083  \uF09E\uF077\uF0CE\uF029  \uF023\uF05C\uF08D\uF0C5\uF05F\uF024\uF073\uF0F9  \uF023\uF059\uF091\uF024\uF0A4\uF0FF\uF09F\uF032  \uF0C7\uF0CB\uF0D0\uF0C8  \uF07B  [نوح]،  هذا  موضع  العلم  الحقيقي  الذي  لا  يتبدل،  ولو  علم  محمد  صلي  الله  عليه  وسلم  من  قومه  ما  علم  نوح  من  قومه،  ما  أمهلهم  طرفة  عين،  ولكن  علم  أن  في  أصلابهم  من  يؤمن  به  ويسعد  بلقاء  ربه،  فقال:  {  اللَّهُمَّ  اغْفِرْ  لِقَوْمِي  فَإنَّهُمْ  لاَ  يَعْلَمُونَ  }   ،  فكل  على  علم  وبينة  من  الله،  فألزم  كل  واحد  ما  ألزم  من  الدعاء؛  ثم  قال:  أليس  كذلك؟،  فقالا:  بلى  .\n" +
                "وقال:\n" +
                "    سمعت  الحديث  الوارد  عن  النبى    صلي  الله  عليه  وسلم:  {  إِنَّهُ  لَيُغَانُ  عَلَىٰ  قَلْبِي.  وَإِنِّي  لأَسْتَغْفِرُ  اللّهَ،  فِي  الْيَوْمِ،  مِائَةَ  مَرَّةٍ  } ،  فأشكل  علي  معناه  فرأيته    صلي  الله  عليه  وسلم  وهو  يقول:  يا  مبارك!  ذلك  غين  الأنوار  لا  غين  الأغيار  .\n" +
                "وقال:  \n" +
                "قلت  لرجل  قد  أحاط  به  الهم  والغم،  حتى  كاد  يمنعه  من  الأكل  والشرب  والنوم:\n" +
                "  يابن  فلان:  اسكن  لقضاء  الله،  وعلق  قلبك  بالله،  ولا  تيأس  من  روح  الله،  وانتظر  الفرج  من  الله،  وإياك  والشرك  بالله،  والنفاق  مع  رسول  الله  صلي  الله  عليه  وسلم،  وسوء  الظن  بالله،  فإنها  موجبة  لدوائر  السوء  من  الله،  وغضبه  ولعنته  وإعداد  ناره،  وأعد  لهم  جهنم  وساءت  مصيرا.\n" +
                "فرأيت  أسيراً  مربوطاً  بين  يدي  رسول  الله  صلي  الله  عليه  وسلم،  وهو  يتلو:\uF07D  \uF024\uF070\uF06B\uF09A\uF089صلي  الله  عليه  وسلم\uF027\uF0AF\uF0BBرضي  الله  عنه\uF083  \uF090\uF0D3\uF0C9\uF03C•\uF05A\uF039\uF024\uF023  \uF040\uF0E8\uF025  \uF060رضي  الله  عنهم\uF04A\uF0CF\uF06A\uF039  \uF0FE\uF092\uF0CE\uF0FB  \uF04E\uF0E4\uF033\uF083\uF0CF\uF089\uF0F7\uF083صلي  الله  عليه  وسلم\uF026  \uF09A\uF0C6\uF0CF\uF069\uF042  \uF023\uF093رضي  الله  عنه\uF08D\uF0F3\uF099\uF046\uF07B\uF024\uF023  \uF062\uF0CE\uF029  \uF0C4\uF04E\uF06E\uF03D\uF0F7\uF0E8رضي  الله  عنه\uF083  \uF0AA\uF021\uF024\uF023  \uF092\uF0CE\uF0FB  \uF0F6\uF04E\uF0E4\uF033\uF0CE\uF02F\uF071\uF0E8\uF03D\uF0E8\uF025  \uF023\uF05A\uF08E\uF0F6\uF08Dرضي  الله  عنهم\uF07A  \uF0F6\uF04E\uF0E4\uF033\uF0CF\uF03F\uF0F7\uF073\uF0E3\uF083  \uF023\uF05A\uF08E\uF0F6\uF08Dرضي  الله  عنهم\uF07A  \uF021\uF024\uF0A3\uF04A\uF0CF\uF069\uF042  \uF078\uF08B\uF0CF\uF07B\uF0E9\uF026  \uF0F6\uF04E\uF0E0\uF036\uF05A\uF0CF\uF042  \uF0F6\uF08D\uF0CF\uF0FF\uF0F8\uF0F3رضي  الله  عنه\uF083\uF075صلي  الله  عليه  وسلم  \uF0F6\uF04E\uF0E4\uF033\uF073\uF039  \uF033  \uF0AA\uF021\uF024\uF023\uF075صلي  الله  عليه  وسلم  \uF0D6\uF091\uF071\uF0E0\uF0FF\uF078\uF0EE  \uF0D2\uF04F\uF08B\uF0CF\uF06D\uF0A7\uF091  \uF0C7\uF0D0\uF0C9\uF0C8  \uF062\uF0CE\uF029\uF075صلي  الله  عليه  وسلم  \uF028\uF023صلي  الله  عليه  وسلم\uF0DF\uF089\uF083\uF0CC\uF08D\uF0E3\uF083  رضي  الله  عنهم\uF037رضي  الله  عنه\uF047رضي  الله  عنه\uF052\uF024\uF075\uF08B\uF0C5\uF07A  \uF0F4\uF089\uF073\uF029\uF073\uF0F9  \uF028\uF023\uF071\uF0E7\uF052\uF024رضي  الله  عنهم\uF07A  \uF0A9\uF021\uF024\uF023  \uF060\uF0CF\uF042  \uF0E3\uF040\uF0F6\uF036\uF073\uF025  \uF07A\uF060\uF073\uF033\uF0F8\uF042صلي  الله  عليه  وسلم\uF027\uF073\uF0F9  \uF0F6\uF04E\uF0E5\uF06B\uF0F7\uF05D\uF0CF\uF042  \uF033  \uF0AA\uF021\uF024\uF023\uF075صلي  الله  عليه  وسلم  \uF0ED\uF04F\uF08A\uF0CE\uF03Dرضي  الله  عنه\uF0E6  \uF0ED\uF04F\uF08A\uF0C5\uF033رضي  الله  عنهم\uF06D  \uF0C7\uF0D0\uF0CA\uF0C8  \uF07B  [الأنفال]\n" +
                "فقلت:  ما  النفاق  مع  رسول  الله  صلي  الله  عليه  وسلم؟،  قال:\n" +
                "التظاهر  بالسنة  والله  يعلم  منك  غير  الذي  قلت.\n" +
                "قلت:  وما  الشرك  بالله؟،  قال:\n" +
                "اتخاذ  الأولياء  والشفعاء  دون  الله  ...  \uF07D  \uF024رضي  الله  عنه\uF042  \uF04E\uF0E4\uF033\uF073\uF039  \uF060\uF0CF\uF069\uF042  \uF0BE\uF0CF\uF06D\uF0CF\uF052صلي  الله  عليه  وسلم\uF0DF\uF08A  \uF060\uF0CF\uF042  \uF03C\uF063\uF092\uF0CD\uF03C\uF075صلي  الله  عليه  وسلم  \uF09F\uF077\uF075صلي  الله  عليه  وسلم  \uF03F\uF0EC\uF08B\uF0CF\uF0FF\uF078\uF0A9  \uF034  \uF09F\uF078\uF073\uF0F9صلي  الله  عليه  وسلم\uF026  رضي  الله  عنه\uF062صلي  الله  عليه  وسلم\uF0E3\uF08D\uF0A9\uF02E\uF078\uF08Bرضي  الله  عنه\uF046\uF073\uF03F  \uF0C7\uF0CD\uF0C8  \uF07B  [السجدة]،  \uF07D  \uF0CF\uF051صلي  الله  عليه  وسلم\uF026  \uF028\uF023صلي  الله  عليه  وسلم\uF0E4\uF08B\uF073\uF083\uF0AA\uF042\uF024\uF023  \uF060\uF0CF\uF042  \uF0C8\uF062صلي  الله  عليه  وسلم\uF0DF\uF08A  \uF0AB\uF021\uF024\uF023  \uF075\uF0E4\uF021\uF024رضي  الله  عنهم\uF0E8\uF078\uF0FF\uF0E4\uF0A9  \uF034  \uF0F6\uF040\uF0E8\uF025  \uF0F6\uF071\uF073\uF039\uF075صلي  الله  عليه  وسلمصلي  الله  عليه  وسلم\uF026  \uF028\uF023\uF071\uF0E7\uF052\uF024\uF09F\uF032  \uF09F\uF077  رضي  الله  عنه\uF062\uF071\uF0E4\uF033\uF0CE\uF03D\uF0F4\uF04Aرضي  الله  عنه\uF083  \uF024\uF05C\uF0AB\uF0F8\uF08B\uF078\uF0A9  \uF09F\uF077\uF075صلي  الله  عليه  وسلم  \uF09A\uF063\uF071\uF0E8\uF03D\uF0C9\uF029\uF0F7\uF0E8رضي  الله  عنه\uF083  \uF0C7\uF0CD\uF0CC\uF0C8  \uF07B  [الزمر]،  قال  رسول  الله  صلي  الله  عليه  وسلم:  {  اشْفَعُوا  تُؤْجَرُوا  } .\n" +
                "قال:  فِ  (  أى  أوْفِ  أو  أكْمِل  )  حقاً  بحق،  حيث  أمرك  الله  ورسوله  بحق،  وقد  بين  لك  حق  البيان،  بقوله:  {تؤجروا}  أفمن  شفع  في  المعصية،  أو  في  طلب  الجاه  والمنزلة،  أو  في  طلب  الدنيا  بالرغبة  يؤجر؟!  بل  يعذب  ..  ذلك  ..  ويتوب  الله  على  من  يشاء...  \n" +
                "قال  الشيخ  أبو  العباس:\n" +
                "كنت  مع  الشيخ  أبى  الحسن  بالقيروان،  وكان  شهر  رمضان،  وكان  ليلة  جمعة،  وكانت  ليلة  سبع  وعشرين،  فذهب  الشيخ  إلى  الجامع  وذهبت  معه،  فلما  دخل  الجامع  وأحرم  رأيت  الأولياء  يتساقطون  عليه،  كما  يتساقط  الذباب  على  العسل،  فلما  أصبحنا  وخرجنا  من  الجامع،  قال  الشيخ:\n" +
                "  ما  كانت  البارحة  إلا  ليلة  عظيمة،  وكانت  ليلة  القدر،  ورأيت  الرسول  صلي  الله  عليه  وسلم  وهو  يقول:    يا  علىّ:  طهر  ثيابك  من  الدنس  تحظ  بمدد  الله  في  كل  نفس  .\n" +
                "قلت:  يا  رسول  الله،  وما  ثيابي؟  قال:\n" +
                "اعلم  أن  الله  قد  خلع  عليك  خمس  خلع:  خلعة  المحبة،  وخلعة  المعرفة،  وخلعة  التوحيد،  وخلعة  الإيمان،  وخلعة  الإسلام.\n" +
                "فمن  أحب  الله،  هان  عليه  كل  شيء.\n" +
                "ومن  عرف  الله،  صغر  لديه  كل  شيء.\n" +
                "ومن  وحد  الله،  لم  يشرك  به  شيئاً.\n" +
                "ومن  آمن  بالله،  أمن  من  كل  شيء.\n" +
                "ومن  أسلم  لله  قلما  يعصيه....\n" +
                "وإن  عصاه  اعتذر  إليه،  وإن  اعتذر  إليه  قبل  عذره.\n" +
                "ففهمت  من  ذلك  معنى  قوله:  \uF07D  رضي  الله  عنهم\uF037رضي  الله  عنه\uF02F\uF024\uF075\uF08B\uF0CF\uF04F\uF075صلي  الله  عليه  وسلم  \uF0F6\uF08D\uF0CE  \uF067\uF073\uF0DC\uF073\uF0F9  \uF0C7\uF0CD\uF0C8    \uF07B  [المدثر]\n" +
                "وهكذا  ظل  رضي  الله  عنه  يتنعم  بالمواجهات  النبوية  حتى  وصل  في  صفاء  القرب  من  رسول  الله  صلي  الله  عليه  وسلم  إلى  حال  عظيم  يعبر  عنه  رضي  الله  عنه  فيقول:\n" +
                "  والله  لو  حجب  عنى  رسول  الله  صلي  الله  عليه  وسلم  طرفة  عين،  ما  عددت  نفسي  من  المسلمين\n");

        text3.setText("نسمات  الأصفياء  رضي  الله  عنهم\nوكان  رضي  الله  عنه  بجانب  المؤانسات  الالهية،  والمخاطبات  الربانية،  والمرائى  النبوية،  تهب  عليه  نسمات  أرواح  الأصفياء  من  النبيين  والصديقين  والصالحين،  ليقتبس  منها  فوائد،  ويغترف  منها  عوارف  وحكم،  ومن  ذلك:\n" +
                "قال  رضي  الله  عنه  :\n" +
                "  رأيت  الصديق  فى  المنام،  فقال  لي:    أتدري  ما  علامة  خروج  حب  الدنيا  من  القلب؟،  قلت:  لا  أدرى،  قال:    علامة  خروج  حب  الدنيا  من  القلب،  بذلها  عند  الوجد،  ووجود  الراحة  منها  عند  الفقد  .\n" +
                "وقال:\n" +
                "  قرأت  ليلة  فى  وردى،  قوله  تعالى:  \uF07D  \uF091\uF040\uF0E4\uF02E  \uF0F4\uF060رضي  الله  عنه\uF042  \uF024\uF070\uF06B\uF0F6\uF08E\uF06E\uF03Dرضي  الله  عنه\uF0E6  \uF035\uF062\uF024\uF073\uF0F9  \uF0C7\uF0CB\uF0CF\uF0C8  \uF034\uF092\uF073\uF02B\uF0F6\uF037رضي  الله  عنه\uF083\uF075صلي  الله  عليه  وسلم  \uF0E7\uF06D\uF0F4\uF05F\uF075صلي  الله  عليه  وسلم  رضي  الله  عنهم\uF037\uF0CE\uF06E\uF02F\uF075\uF091  صلي  الله  عليه  وسلم\uF0E8\uF08C  \uF0C8\uF040\uF0BB\uF06E\uF03D\uF070\uF067\uF0F8\uF03A\uF024\uF023  \uF0CF\uF051\uF023رضي  الله  عنه\uF08D\uF0F8\uF02E\uF04D\uF07D\uF024\uF023\uF075صلي  الله  عليه  وسلم  \uF0C7\uF0CB\uF0D0\uF0C8    \uF07B  [الرحمن]،  فرأيت  أبا  بكر  رضي  الله  عنه  فى  المنام،  فقال  لي:  صل  من  يبقى،  واهجر  من  يفنى،  تُجَلّْ  وتكرم  ..  تجل  عن  الفناء،  وتكرم  بالبقاء  .\n" +
                "وقال:\n" +
                "    آذانى  إنسان  مرة،  فضقت  ذرعاً  بذلك،  فنمت،  فرأيت  قائلاً  يقول  لي:    من  علامة  الصدّيقية:  كثرة  أعدائها،  ثم  لا  يبالي  بهم  \n" +
                "وقال:\n" +
                "    هممت  مرة،  أن  أختار  القلة  من  الدنيا  على  الكثرة،  ثم  أمسكت  وخشيت  سوء  الأدب،  فلجأت  إلى  ربي،  ورأيت  فى  النوم،  كأن  سليمان  عليه  السلام،  على  سرير  جالس  وحوله  عساكر،  ورَفع  لى  عن  قدوره  وجفانه  فرأيت  أمرا  كما  وصفه  الله  بقوله:  \uF07D  \uF035\uF062\uF024\uF078\uF0FF\uF0C5\uF05F\uF075صلي  الله  عليه  وسلم  \uF0C9\uF03E\uF023\uF075\uF071\uF070\uF067\uF0F8\uF03A\uF024\uF025\uF078\uF02E  \uF039\uF091صلي  الله  عليه  وسلم\uF0DF\uF089\uF0E8\uF025\uF075صلي  الله  عليه  وسلم  \uF034\uF042\uF04D\uF0BB\uF075\uF08B\uF0C5\uF099\uF023\uF0A7\uF091    \uF07B  [13سبأ]،  فنوديت:  لا  تختر  مع  الله  شيئاً،  وإن  اخترت  فاختر  العبودية  لله،  اقتداءا  برسوله  صلي  الله  عليه  وسلم  حيث  قال:  \uF07D.  \uF023\uF059\uF089\uF0F6\uF036رضي  الله  عنه\uF0E3  \uF023\uF059\uF091\uF071\uF0E4\uF033\uF078\uF0A9  \uF0C7\uF0CC\uF0C8    \uF07B  [الإسراء]،  وإن  كان  ولا  بد،  فاختر  ألا  تختار،  وفر  من  ذلك  المختار،  إلى  اختيار  الله  \n" +
                "فانتبهت  من  نومي،  فرأيت  بعدها  قائلاً  يقول  لي:\n" +
                "  إن  الله  اختار  لك  أن  تقول:  اللهم  وسع  على  رزقى  من  دنياي،  ولا  تحجبني  بها  عن  أخراي،  واجعل  مقامي  عندك  دائماً  بين  يديك  وناظراً  منك  إليك،  وأرني  وجهك،  ووارني  عن  الرؤية،  وعن  كل  شيء  دونك،  وارفع  البين  فيما  بيني  وبينك،  يا  من  هو  الأول  والآخر  والظاهر  والباطن  وهو  بكل  شيء  عليم  \n" +
                "وقال:\n" +
                "  رأيت  كأني  في  علّيين  مع  الملائكة  المقربين،  فى  نعيم  لا  أبغى  عنه  بدلاً؛  فقالوا:    سر  إلى  الزيادة    فسرت  معهم،  فدخلت  فى  موطن  كريم  لا  أقدر  على  وصفه  طامعاً  فى  الشهود،  فإذا  أنا  بشهود  لا  أقدر  على  وصفه،  فقيل  لي:\n" +
                "  من  كففت  جوارحه  عن  معصيتي،  وزيّنته  بحفظ  أمانتى،  وفتحت  قلبه  لمشاهدتى،  وأطلقت  لسان  سرّه  لمناجاتى،  ورفعت  الحجاب  بينه  وبين  صفاتي،  وأشهدته  معاني  أرواح  كلماتي،  فقد  زحزحته  عن  النار،  وأدخلته  جنتى،  وفاز  بقربي،  وصحبة  ملائكتي    \uF07D  \uF060رضي  الله  عنهم\uF04A\uF073\uF0F9  رضي  الله  عنهمرضي  الله  عنهم\uF0CC\uF093\uF0F4\uF06D\uF0E3\uF097  \uF0C7\uF060رضي  الله  عنه\uF0E3  \uF0CD\uF091\uF024•\uF059\uF039\uF024\uF023  \uF09F\uF040\uF0C5\uF07A\uF0F7\uF08A\uF0E9\uF026\uF075صلي  الله  عليه  وسلم  \uF073\uF070•\uF059رضي  الله  عنهم  \uF0F8\uF039\uF024\uF023  \uF0F4\uF089\uF073\uF029\uF073\uF0F9  رضي  الله  عنهم\uF097\uF024\uF073\uF0F9    \uF07B  [185آل  عمران]  .\n" +
                "وقال:\n" +
                "  رأيت  في  النوم  صائحاً  يصيح  من  جو  السماء:\n" +
                "    وإنما  تساق  إلى  رزقك،  أو  لأجلك،  أو  لما  يقضى  الله  به  عليك،  أو  بك،  أو  لك؛  وهى  خمسة  لا  سادس  لها؛  فاتق  الله  أينما  كنت،  ولا  تعدل  بالتقوى  شيئاً،  فإن  العاقبة  للمتقين،  والحق  يحبهم  ويحبونه.\n" +
                "\uF07D  رضي  الله  عنهم\uF037\uF0CF\uF039\uF0BA\uF073\uF08C  \uF0E3\uF040\uF0F4\uF0D2\uF073\uF0F9  \uF0AB\uF021\uF024\uF023  \uF0CF\uF06D\uF08A\uF0CF\uF03F\uF0F7\uF073\uF0E3\uF083  \uF060رضي  الله  عنه\uF042  \uF0E2\uF0E4\uF021\uF024رضي  الله  عنه\uF0B1\uF06F\uF084  \uF034  \uF0AA\uF021\uF024\uF023\uF075صلي  الله  عليه  وسلم  \uF0EC\uF0EC\uF0C5\uF099\uF0BA\uF075صلي  الله  عليه  وسلم  \uF0ED\uF04F\uF08A\uF0CE\uF03Dرضي  الله  عنه\uF0E6  \uF0C7\uF0CE\uF0CD\uF0C8  \uF07B  [المائدة]،  أعوذ  بالله  من  سوء  القضاء،  ومن  جزع  النفس  عند  ورود  البلاء،  ومن  الفرح  والحزن،  والهمِّ  والغمِّ  في  الشدة  والرخاء.  .\n" +
                "وقال:  ...  سمعت  قائلاً  يقول:\n" +
                "  ما  صبر  من  أنَّ،  ولا  سلم  من  تكلَّف،  ولا  رضى  من  سأل،  ولا  فوّض  من  دبّر،  ولا  توكل  من  دعا  ،  وهى  خمسة،  وما  أحوجك  إلى  هذه  الخمسة  أن  تموت  عليها  \uF07D  \uF0C9\uF062\uF03E\uF075\uF091  \uF092\uF0CE\uF06F\uF054\uF0CE\uF029  \uF021\uF024رضي  الله  عنهم\uF04A\uF0CF\uF039  \uF07C\uF04D\uF0F8\uF039رضي  الله  عنه\uF093\uF052صلي  الله  عليه  وسلم\uF026  \uF0A5\uF092\uF06E\uF03C\uF0CE\uF029  \uF0F4\uF060\uF0CF\uF042  \uF039\uF08E\uF0F6\uF08Dرضي  الله  عنهم\uF07A  \uF0D7\uF08E\uF08D\uF0C9\uF029\uF073\uF0F9  \uF0C7\uF0CB\uF0CD\uF0C8  \uF07B  [القصص]،  فزدنى  من  فضلك  وإحسانك،  واجعلنى  من  الشاكرين  لنعمائك  \n" +
                "وقال:\n" +
                "    استنار  قلبي  يوماً،  فكنت  أشهد  ملكوت  السموات  السبع،  والأراضين  السبع،  فوقعت  منى  هفوة،  فحجبت  عن  شهود  ذلك،  فتعجبت،  كيف  حجبني  هذا  الأمر  الصغير  عن  هذا  الأمر  الكبير؟  فإذا  قائلٌ  يقول  لي:\n" +
                "  البصيرة  كالبصر،  أدنى  شيء  يحلُّ  فيها  يعطِّل  النظر    .\n" +
                "وقال:  قيل  لي:\n" +
                "    لا  تؤخر  طاعة  وقت  لوقت،  فتعاقب  بفوتها،  أو  بفوت  غيرها  أو  مثلها،  جزاء  لما  كفر  من  ذلك  الوقت،  فإن  لكل  وقت  سهما  فى  العبودية  يقتضيه  الحق  منك  بحكم  الربوبية  .\n" +
                "فقلت  فى  نفسي:\n" +
                "  قد  أخر  الصديق  الوتر  إلى  آخر  الليل    فإذا  هو  بصوت  فى  النوم:  تلك  عادة  جارية،  وسنة  ثابتة،  ألزمه  الله  إياها،  مع  المحافظة  عليها،  فأنى  لك  بها  مع  الميل  إلى  الراحات،  والتمتع  بالشهوات  –  هيهات  –  والدخول  فى  أنواع  المخالفات،  والغفلة  عن  المشاهدات  –  هيهات  ،  فقلت  فى  نفسي:  أتدبير  أم  رفض؟،  فقال:\n" +
                "  بل  تدبير  يقتضى  الأدب،  والتنبيه  لما  أغفل،  وهى  وصية  إليك،  ووصية  منك  لعباده  الصالحين،  فتنبه  لها،  ولا  تكن  من  الغافلين.  \n" +
                "وقال:\n" +
                "    رأيت  أستاذي  تحت  العرش،  فقلت  له:    يا  سيدي  رأيتك  البارحة  تحت  العرش،  فقال  لي:  ما  رأيت  إلا  نفسك  يا  على!  من  كان  مع  الله  بلا  أين،  كيف  يُرى؟  ولكن  إذا  ورثت  مقامي  تراني  \n" +
                "وهكذا  ظل  الشيخ  أبوالحسن  رضي  الله  عنه  يصقل  نفسه  بالمجاهدات  الفادحة  تارة  فى  خلوته  وآونة  فى  سياحاته  ....\n" +
                "ولصدقه  وإخلاصه  كان  الله  عز  وجل  يوجهه  إلى  الأمثل  والأقوم  بذاته  العلية  فى  مقام  السماع  أو  المعاينة  ...،  وكان  يتولى  التوجيه  أيضاً  الرسول  صلي  الله  عليه  وسلم  والأنبياء  والصديق  رضي  الله  عنه.\n" +
                "وفى  بعض  الأحيان  كان  التوجيه  يتم  بالهواتف  الحقة  سماعاً  ظاهراً؛  أو  إلهاماً  قلبياً،  وأحياناً  كان  الله  عز  وجل  يقيض  له  بعض  الصالحين  الأحياء  للتنبيه  والأخذ  باليد  ...\n" +
                "يقول  في  ذلك  رضي  الله  عنه:\n" +
                "  جعت  مرة  ثمانين  يوماً،  فخطر  لي  أن  قد  حصل  لى  من  الأمر  شيء،  وإذا  بامرأة  خارجة  من  مغارة،  ووجهها  كأنه  الشمس  حسناً،  وهي  تقول:  منحوس،  منحوس!  جاع  ثمانين  يوماً،  فأخذ  يدل  على  الله  بعمله،  وها  أنا  لي  ستة  أشهر  لم  أذق  طعاما  .\n");
    }
}
